            }
            ]
            

    
constructor(){
        super();
        console.log("Hello I am Constructor from news component")
        this.state={
            articles:[],
            loading:false,
            page:1
            

    }

        
    }
     async componentDidMount(){
        let url=`https://newsapi.org/v2/top-headlines?country=in&category=business&apiKey=c1d68e0308ac4f268134f00f74e8c44b&pagesize=${this.props.pageSize}`
        let data= await fetch(url)
        let parsedData= await data.json()
        console.log(parsedData)
        this.setState({articles:parsedData.articles,totalresult:parsedData.totalResults})
        
    
    }
      handlePrevClick=async()=>{
        let url=`https://newsapi.org/v2/top-headlines?country=in&category=business&apiKey=c1d68e0308ac4f268134f00f74e8c44b&page=${this.state.page-1}&pagesize=${this.props.pageSize}`
        let data= await fetch(url)
        let parsedData= await data.json()
        console.log(parsedData)
        

         this.setState({
         
             page:this.state.page-1,
             articles:parsedData.articles
         })

    }
      handleNextClick=async()=>{
          if(this.state.page+1>Math.ceil(this.state.totalresult/this.props.pageSize)){

          }
          else{
        let url=`https://newsapi.org/v2/top-headlines?country=in&category=business&apiKey=c1d68e0308ac4f268134f00f74e8c44b&page=${this.state.page+1}&pagesize=${this.props.pageSize}`
        let data= await fetch(url)
        let parsedData= await data.json()
        console.log(parsedData)
        console.log("clicked prev")
        

         this.setState({
         
             page:this.state.page+1,
             articles:parsedData.articles
         })
        }

    }
    render() {
        return (
            <div className="container my-3">
                
                <h1 className="text-center">Mumbai Today-Top Headlines</h1>
                <Spinner></Spinner>
                <div className="row">
                    
                {this.state.articles.map((element)=>{
                    return <div className="col-md-4" key={element.url}>
                     <Newsitem  title={element.title?element.title.slice(0,45):""} description={element.description?element.description.slice(0,88):""} imageUrl={element.urlToImage} newsUrl={element.url}></Newsitem>
                     </div>

                })}


                   
                 
               
              
                </div>
                <div className="container d-flex justify-content-between">
                <button disabled={this.state.page<=1} type="button" className="btn btn-dark" onClick={this.handlePrevClick}> &larr;Previous</button>
                <button disabled={this.state.page+1>Math.ceil(this.state.totalresult/this.props.pageSize)} type="button" className="btn btn-dark" onClick={this.handleNextClick}>Next &rarr;</button>
                </div>
            
                
            </div>
        )
    }
}

export default News
